const fetch = require('node-fetch')

module.exports.Glitch = function Glitch(text, text2) {
	return new Promise((resolve, reject) => {
		fetch(encodeURI(`https://vip-api-zahirr.herokuapp.com/api/textmaker?text=${text}&text2=${text2}&theme=glitch&apikey=zahirgans`))
        .then(response => response.json())
		.then(resolve)
		.catch(reject)
	})
}

