const TikTokScraper = require('tiktok-scraper');


async function TiktokData(username) {
	const user = await TikTokScraper.getUserProfileInfo(username);
    return ({
		Status: 200,
		Results: user,
	});
}

const TiktokStalk = (username) => new Promise((resolve, reject) => {
    if (username === 'undefined') { reject('masukan user nya kak.') }
    try {
		TiktokData(username).then(data => {
			resolve(data);
		});
    } catch (error) {
        reject({
			code:400,
			message: error
		});
    }
})

module.exports = TiktokStalk
