const fetch = require('node-fetch')
const facebook = 'https://fb-api-zhirrr.vercel.app'

function Fbdown(query) {
	return new Promise((resolve, reject) => {
		fetch(facebook+'/?url=' + encodeURIComponent(query))
		.then(res => res.json())
		.then(resolve)
		.catch(reject)
	})
}

module.exports = Fbdown
